import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";
import helmet from "helmet";
import rateLimit from "express-rate-limit";
import cookieParser from "cookie-parser";
import { doubleCsrf } from "csrf-csrf";
import { randomBytes } from 'crypto';
import path from 'path';
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

const app = express();

// Basic security headers with updated CSP
app.use(helmet({
  contentSecurityPolicy: {
    directives: {
      defaultSrc: ["'self'"],
      scriptSrc: [
        "'self'",
        "'unsafe-inline'",
        "'unsafe-eval'",
        "https://www.google.com",
        "https://www.gstatic.com",
        "https://*.google.com",
        "https://*.gstatic.com"
      ],
      frameSrc: [
        "'self'",
        "https://www.google.com",
        "https://recaptcha.google.com",
        "https://www.recaptcha.net"
      ],
      styleSrc: ["'self'", "'unsafe-inline'", "https://www.gstatic.com"],
      imgSrc: ["'self'", "data:", "https:", "blob:"],
      connectSrc: [
        "'self'",
        "https://www.google.com",
        "https://*.google.com",
        "https://www.gstatic.com",
        "https://*.gstatic.com",
        "ws:",
        "wss:"
      ],
      workerSrc: ["'self'", "blob:"],
      childSrc: ["'self'", "blob:", "https://www.google.com"],
      formAction: ["'self'", "https://www.google.com"],
      upgradeInsecureRequests: process.env.NODE_ENV === 'production' ? [] : null,
    },
  },
  crossOriginEmbedderPolicy: false,
  crossOriginResourcePolicy: { policy: "cross-origin" },
}));

// Additional security headers
app.use((req, res, next) => {
  res.setHeader("X-Content-Type-Options", "nosniff");
  res.setHeader("X-Frame-Options", "DENY");
  res.setHeader("X-XSS-Protection", "1; mode=block");
  next();
});

// API rate limiting
const limiter = rateLimit({
  windowMs: 15 * 60 * 1000,
  max: 100,
});
app.use("/api/", limiter);

// Body parsing
app.use(express.json());
app.use(express.urlencoded({ extended: false }));
app.use(cookieParser());

// CSRF protection
const { generateToken, doubleCsrfProtection } = doubleCsrf({
  getSecret: () => process.env.SESSION_SECRET || randomBytes(32).toString('hex'),
  cookieName: "x-csrf-token",
  cookieOptions: {
    httpOnly: true,
    sameSite: "lax",
    secure: process.env.NODE_ENV === "production",
  },
});

app.use('/api', doubleCsrfProtection);
app.get('/api/csrf-token', (req, res) => {
  res.json({ csrfToken: generateToken(req, res) });
});

// Request logging
app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        const sanitizedResponse = { ...capturedJsonResponse };
        delete sanitizedResponse.password;
        delete sanitizedResponse.token;
        delete sanitizedResponse.csrfToken;
        logLine += ` :: ${JSON.stringify(sanitizedResponse)}`;
      }
      log(logLine.length > 80 ? logLine.slice(0, 79) + "…" : logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  if (process.env.NODE_ENV === 'production') {
    // Serve static files in production
    const publicPath = path.resolve(__dirname, '../dist/public');
    app.use(express.static(publicPath));

    // Production SPA fallback
    app.use('*', (_req: Request, res: Response) => {
      res.sendFile('index.html', { 
        root: publicPath,
        headers: {
          'Cache-Control': 'no-cache, no-store, must-revalidate',
          'Pragma': 'no-cache',
          'Expires': '0'
        }
      });
    });
  } else {
    // Development mode - let Vite handle everything
    await setupVite(app, server);
  }

  const port = 5000;
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express, Request, Response, NextFunction } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { 
  insertLeadSchema, 
  insertUserProfileSchema, 
  insertInvestmentPlanSchema,
  leads,
  users,
  userProfiles,
  investmentPlans 
} from "@shared/schema";
import fetch from "node-fetch";
import { setupAuth } from "./auth";
import { db } from "./db";
import { desc, eq, and } from "drizzle-orm";
import { z } from "zod";
import helmet from "helmet";
import rateLimit from "express-rate-limit";

// Type for structured error responses
interface ApiError {
  message: string;
  code?: string;
  details?: unknown;
}

// Custom error class for API errors
class ApiError extends Error {
  constructor(
    message: string,
    public statusCode: number = 500,
    public code?: string,
    public details?: unknown
  ) {
    super(message);
    this.name = 'ApiError';
  }
}

// Strict type checking for reCAPTCHA response
interface RecaptchaResponse {
  success: boolean;
  challenge_ts?: string;
  hostname?: string;
  "error-codes"?: string[];
}

async function verifyRecaptcha(token: string): Promise<boolean> {
  const recaptchaSecret = process.env.RECAPTCHA_SECRET_KEY;
  if (!recaptchaSecret) {
    console.error("Missing RECAPTCHA_SECRET_KEY environment variable");
    throw new ApiError("reCAPTCHA configuration error", 500, "RECAPTCHA_CONFIG_ERROR");
  }

  try {
    console.log("Verifying reCAPTCHA token...", { tokenLength: token?.length });
    const verificationUrl = "https://www.google.com/recaptcha/api/siteverify";
    const params = new URLSearchParams({
      secret: recaptchaSecret,
      response: token
    });

    const response = await fetch(verificationUrl, {
      method: "POST",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
      },
      body: params.toString(),
    });

    if (!response.ok) {
      console.error("reCAPTCHA API error:", response.status, await response.text());
      throw new ApiError("reCAPTCHA verification API error", 500, "RECAPTCHA_API_ERROR");
    }

    const data = await response.json() as RecaptchaResponse;
    console.log("reCAPTCHA verification response:", {
      success: data.success,
      hostname: data.hostname,
      errorCodes: data["error-codes"]
    });

    if (!data.success) {
      console.error("reCAPTCHA verification failed:", data["error-codes"]);
      throw new ApiError(
        "reCAPTCHA verification failed",
        400,
        "RECAPTCHA_VERIFICATION_FAILED",
        data["error-codes"]
      );
    }

    return data.success;
  } catch (error) {
    console.error("reCAPTCHA verification error:", error);
    if (error instanceof ApiError) throw error;
    throw new ApiError("Failed to verify reCAPTCHA", 500, "RECAPTCHA_ERROR");
  }
}

function requireAuth(req: Request, res: Response, next: NextFunction) {
  if (!req.isAuthenticated()) {
    return res.status(401).json({ message: "Unauthorized" });
  }
  next();
}

// Error handling middleware
function errorHandler(
  err: Error,
  req: Request,
  res: Response,
  next: NextFunction
) {
  console.error(err);

  if (err instanceof ApiError) {
    return res.status(err.statusCode).json({
      message: err.message,
      code: err.code,
      details: err.details,
    });
  }

  if (err instanceof z.ZodError) {
    return res.status(400).json({
      message: "Validation error",
      code: "VALIDATION_ERROR",
      details: err.errors,
    });
  }

  // Don't expose internal errors to client
  res.status(500).json({
    message: "Internal server error",
    code: "INTERNAL_ERROR",
  });
}

export async function registerRoutes(app: Express): Promise<Server> {
  // Security middleware with updated CSP
  app.use(helmet({
    contentSecurityPolicy: false, // We'll use the main CSP config from index.ts
  }));

  // Rate limiting for public endpoints
  const publicLimiter = rateLimit({
    windowMs: 15 * 60 * 1000, // 15 minutes
    limit: 100, // Limit each IP to 100 requests per windowMs
    standardHeaders: 'draft-7',
    legacyHeaders: false,
    message: { message: "Too many requests, please try again later" }
  });

  // Rate limiting for admin endpoints
  const adminLimiter = rateLimit({
    windowMs: 15 * 60 * 1000,
    limit: 300,
    standardHeaders: 'draft-7',
    legacyHeaders: false,
    message: { message: "Too many requests, please try again later" }
  });

  // Rate limiting for user endpoints
  const userLimiter = rateLimit({
    windowMs: 15 * 60 * 1000,
    limit: 200,
    standardHeaders: 'draft-7',
    legacyHeaders: false,
    message: { message: "Too many requests, please try again later" }
  });

  // Set up authentication
  setupAuth(app);

  // Check if user is admin middleware
  function requireAdmin(req: Request, res: Response, next: NextFunction) {
    if (!req.isAuthenticated() || !req.user.isAdmin) {
      return res.status(403).json({ message: "Forbidden: Admin access required" });
    }
    next();
  }

  // Public routes with rate limiting
  app.post("/api/leads", publicLimiter, async (req, res, next) => {
    try {
      const { captchaToken, ...leadData } = req.body;

      // Validate lead data
      const validatedData = insertLeadSchema.parse(leadData);

      // Verify reCAPTCHA
      if (!captchaToken) {
        throw new ApiError("reCAPTCHA token is required", 400, "MISSING_CAPTCHA");
      }

      const isValidCaptcha = await verifyRecaptcha(captchaToken);
      if (!isValidCaptcha) {
        throw new ApiError("Invalid reCAPTCHA", 400, "INVALID_CAPTCHA");
      }

      // Store lead
      const lead = await storage.createLead(validatedData);
      res.json(lead);
    } catch (error) {
      next(error);
    }
  });

  // Public routes - Investment plans
  app.get("/api/investment-plans", publicLimiter, async (req, res, next) => {
    try {
      // Only active plans are visible to the public
      const plans = await storage.getInvestmentPlans(true);
      res.json(plans);
    } catch (error) {
      next(error);
    }
  });

  app.get("/api/investment-plans/:id", publicLimiter, async (req, res, next) => {
    try {
      const id = parseInt(req.params.id);
      if (isNaN(id)) {
        throw new ApiError("Invalid ID", 400, "INVALID_ID");
      }

      const plan = await storage.getInvestmentPlanById(id);
      if (!plan || !plan.isActive) {
        throw new ApiError("Investment plan not found", 404, "NOT_FOUND");
      }

      res.json(plan);
    } catch (error) {
      next(error);
    }
  });

  // User profile routes - For authenticated users
  app.get("/api/user/profile", requireAuth, userLimiter, async (req, res, next) => {
    try {
      if (!req.user) {
        return res.status(401).json({ message: "Unauthorized" });
      }
      const profile = await storage.getUserProfile(req.user.id);
      res.json(profile || { userId: req.user.id });
    } catch (error) {
      next(error);
    }
  });

  app.post("/api/user/profile", requireAuth, userLimiter, async (req, res, next) => {
    try {
      if (!req.user) {
        return res.status(401).json({ message: "Unauthorized" });
      }

      // Validate profile data
      const validatedData = insertUserProfileSchema.parse(req.body);

      // Check if profile already exists
      const existingProfile = await storage.getUserProfile(req.user.id);

      let profile;
      if (existingProfile) {
        // Update existing profile
        profile = await storage.updateUserProfile(req.user.id, validatedData);
      } else {
        // Create new profile
        profile = await storage.createUserProfile({
          ...validatedData,
          userId: req.user.id
        });

        // Update user to mark profile as completed
        await storage.updateUser(req.user.id, { profileCompleted: true });
      }

      res.json(profile);
    } catch (error) {
      next(error);
    }
  });

  // Admin routes - Leads management
  app.get("/api/admin/leads", requireAuth, requireAdmin, adminLimiter, async (req, res, next) => {
    try {
      console.log("Fetching leads for admin user:", req.user);
      const allLeads = await db.select().from(leads).orderBy(desc(leads.createdAt));
      console.log(`Found ${allLeads.length} leads`);
      res.json(allLeads);
    } catch (error) {
      console.error("Error fetching leads:", error);
      next(error);
    }
  });

  app.delete("/api/admin/leads/:id", requireAuth, requireAdmin, adminLimiter, async (req, res, next) => {
    try {
      const id = parseInt(req.params.id);
      if (isNaN(id)) {
        throw new ApiError("Invalid ID", 400, "INVALID_ID");
      }

      const [deletedLead] = await db.delete(leads)
        .where(eq(leads.id, id))
        .returning();

      if (!deletedLead) {
        throw new ApiError("Lead not found", 404, "NOT_FOUND");
      }

      console.log(`Deleted lead with ID: ${id}`);
      res.json(deletedLead);
    } catch (error) {
      console.error("Error deleting lead:", error);
      next(error);
    }
  });

  // Admin routes - Investment plans management
  app.get("/api/admin/investment-plans", requireAuth, requireAdmin, adminLimiter, async (req, res, next) => {
    try {
      // Admin can see all plans including inactive ones
      const plans = await storage.getInvestmentPlans(false);
      res.json(plans);
    } catch (error) {
      next(error);
    }
  });

  app.post("/api/admin/investment-plans", requireAuth, requireAdmin, adminLimiter, async (req, res, next) => {
    try {
      // Validate plan data
      const validatedData = insertInvestmentPlanSchema.parse(req.body);

      // Create new plan
      const plan = await storage.createInvestmentPlan(validatedData);
      res.status(201).json(plan);
    } catch (error) {
      next(error);
    }
  });

  app.patch("/api/admin/investment-plans/:id", requireAuth, requireAdmin, adminLimiter, async (req, res, next) => {
    try {
      const id = parseInt(req.params.id);
      if (isNaN(id)) {
        throw new ApiError("Invalid ID", 400, "INVALID_ID");
      }

      // Validate plan data
      // Allow partial updates
      const validatedData = insertInvestmentPlanSchema.partial().parse(req.body);

      // Update plan
      const plan = await storage.updateInvestmentPlan(id, validatedData);
      if (!plan) {
        throw new ApiError("Investment plan not found", 404, "NOT_FOUND");
      }

      res.json(plan);
    } catch (error) {
      next(error);
    }
  });

  // Admin routes - Users management
  app.get("/api/admin/users", requireAuth, requireAdmin, adminLimiter, async (req, res, next) => {
    try {
      const allUsers = await db.select().from(users).orderBy(desc(users.createdAt));
      console.log(`Found ${allUsers.length} users`);
      res.json(allUsers);
    } catch (error) {
      console.error("Error fetching users:", error);
      next(error);
    }
  });

  app.get("/api/admin/users/:id", requireAuth, requireAdmin, adminLimiter, async (req, res, next) => {
    try {
      const id = parseInt(req.params.id);
      if (isNaN(id)) {
        throw new ApiError("Invalid ID", 400, "INVALID_ID");
      }

      const user = await storage.getUser(id);
      if (!user) {
        throw new ApiError("User not found", 404, "NOT_FOUND");
      }

      // Get user profile if it exists
      const profile = await storage.getUserProfile(id);

      // Combine user and profile data
      res.json({
        ...user,
        profile
      });
    } catch (error) {
      next(error);
    }
  });

  // Register error handler
  app.use(errorHandler);

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  users, 
  leads, 
  userProfiles, 
  investmentPlans,
  type User, 
  type InsertUser, 
  type Lead, 
  type InsertLead,
  type UserProfile,
  type InsertUserProfile,
  type InvestmentPlan,
  type InsertInvestmentPlan
} from "@shared/schema";
import { db } from "./db";
import { eq, and } from "drizzle-orm";
import session from "express-session";
import connectPg from "connect-pg-simple";
import { pool } from "./db";

const PostgresSessionStore = connectPg(session);

export interface IStorage {
  // Lead management
  createLead(lead: InsertLead): Promise<Lead>;
  getLeadById(id: number): Promise<Lead | undefined>;
  getLeadByEmail(email: string): Promise<Lead | undefined>;
  markLeadAsConverted(id: number): Promise<Lead | undefined>;

  // User management
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: number, userData: Partial<InsertUser>): Promise<User | undefined>;

  // User profile management
  getUserProfile(userId: number): Promise<UserProfile | undefined>;
  createUserProfile(profile: InsertUserProfile & { userId: number }): Promise<UserProfile>;
  updateUserProfile(userId: number, profileData: Partial<InsertUserProfile>): Promise<UserProfile | undefined>;

  // Investment plan management
  getInvestmentPlans(activeOnly?: boolean): Promise<InvestmentPlan[]>;
  getInvestmentPlanById(id: number): Promise<InvestmentPlan | undefined>;
  createInvestmentPlan(plan: InsertInvestmentPlan): Promise<InvestmentPlan>;
  updateInvestmentPlan(id: number, planData: Partial<InsertInvestmentPlan>): Promise<InvestmentPlan | undefined>;

  // Session store
  sessionStore: session.Store;
}

export class DatabaseStorage implements IStorage {
  sessionStore: session.Store;

  constructor() {
    this.sessionStore = new PostgresSessionStore({
      pool,
      createTableIfMissing: true,
    });
  }

  // Lead management methods
  async createLead(insertLead: InsertLead): Promise<Lead> {
    console.log("Creating new lead in database...");
    const [lead] = await db.insert(leads).values(insertLead).returning();
    console.log(`Lead created with ID: ${lead.id}`);
    return lead;
  }

  async getLeadById(id: number): Promise<Lead | undefined> {
    const [lead] = await db.select().from(leads).where(eq(leads.id, id));
    return lead;
  }

  async getLeadByEmail(email: string): Promise<Lead | undefined> {
    const [lead] = await db.select().from(leads).where(eq(leads.email, email));
    return lead;
  }

  async markLeadAsConverted(id: number): Promise<Lead | undefined> {
    const [lead] = await db
      .update(leads)
      .set({ convertedToUser: true })
      .where(eq(leads.id, id))
      .returning();
    return lead;
  }

  // User management methods
  async getUser(id: number): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db.insert(users).values(insertUser).returning();
    return user;
  }

  async updateUser(id: number, userData: Partial<InsertUser>): Promise<User | undefined> {
    const [updatedUser] = await db
      .update(users)
      .set({ ...userData, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();
    return updatedUser;
  }

  // User profile management methods
  async getUserProfile(userId: number): Promise<UserProfile | undefined> {
    const [profile] = await db
      .select()
      .from(userProfiles)
      .where(eq(userProfiles.userId, userId));
    return profile;
  }

  async createUserProfile(profile: InsertUserProfile & { userId: number }): Promise<UserProfile> {
    const [userProfile] = await db
      .insert(userProfiles)
      .values([profile])
      .returning();

    // Mark user's profile as completed
    await db
      .update(users)
      .set({ profileCompleted: true, updatedAt: new Date() })
      .where(eq(users.id, profile.userId));

    return userProfile;
  }

  async updateUserProfile(userId: number, profileData: Partial<InsertUserProfile>): Promise<UserProfile | undefined> {
    const updateData = { ...profileData, updatedAt: new Date() };
    // Convert number types to strings for decimal fields
    if (updateData.monthlyInvestmentBudget !== undefined) {
      updateData.monthlyInvestmentBudget = updateData.monthlyInvestmentBudget?.toString() as any;
    }
    const [profile] = await db
      .update(userProfiles)
      .set(updateData)
      .where(eq(userProfiles.userId, userId))
      .returning();
    return profile;
  }

  // Investment plan management methods
  async getInvestmentPlans(activeOnly = true): Promise<InvestmentPlan[]> {
    if (activeOnly) {
      return db
        .select()
        .from(investmentPlans)
        .where(eq(investmentPlans.isActive, true));
    }
    return db.select().from(investmentPlans);
  }

  async getInvestmentPlanById(id: number): Promise<InvestmentPlan | undefined> {
    const [plan] = await db
      .select()
      .from(investmentPlans)
      .where(eq(investmentPlans.id, id));
    return plan;
  }

  async createInvestmentPlan(plan: InsertInvestmentPlan): Promise<InvestmentPlan> {
    // Convert number types to strings for decimal fields
    const planData = {
      ...plan,
      minimumInvestment: plan.minimumInvestment?.toString(),
      monthlyReturnRate: plan.monthlyReturnRate?.toString()
    };
    const [investmentPlan] = await db
      .insert(investmentPlans)
      .values([planData])
      .returning();
    return investmentPlan;
  }

  async updateInvestmentPlan(id: number, planData: Partial<InsertInvestmentPlan>): Promise<InvestmentPlan | undefined> {
    const updateData = { ...planData, updatedAt: new Date() };
    // Convert number types to strings for decimal fields
    if (updateData.minimumInvestment !== undefined) {
      updateData.minimumInvestment = updateData.minimumInvestment?.toString() as any;
    }
    if (updateData.monthlyReturnRate !== undefined) {
      updateData.monthlyReturnRate = updateData.monthlyReturnRate?.toString() as any;
    }
    const [plan] = await db
      .update(investmentPlans)
      .set(updateData)
      .where(eq(investmentPlans.id, id))
      .returning();
    return plan;
  }
}

export const storage = new DatabaseStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path, { dirname } from "path";
import { fileURLToPath } from "url";
import { createServer as createViteServer, createLogger } from "vite";
const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        __dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(__dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { pgTable, text, serial, timestamp, varchar, boolean, integer, decimal } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Users table for authentication and profiles
export const users = pgTable("users", {
  id: serial("id").primaryKey(),
  username: varchar("username", { length: 255 }).notNull().unique(),
  password: varchar("password", { length: 255 }).notNull(),
  email: varchar("email", { length: 255 }).notNull().unique(),
  fullName: varchar("full_name", { length: 255 }),
  isAdmin: boolean("is_admin").default(false),
  profileCompleted: boolean("profile_completed").default(false),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// User Profiles with additional information
export const userProfiles = pgTable("user_profiles", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").notNull().references(() => users.id),
  phone: varchar("phone", { length: 20 }),
  country: varchar("country", { length: 100 }),
  preferredLanguage: varchar("preferred_language", { length: 50 }).default("en"),
  riskTolerance: varchar("risk_tolerance", { length: 20 }),
  investmentGoals: varchar("investment_goals", { length: 255 }),
  monthlyInvestmentBudget: decimal("monthly_investment_budget", { precision: 12, scale: 2 }),
  prefersDarkMode: boolean("prefers_dark_mode").default(false),
  preferredFontSize: varchar("preferred_font_size", { length: 20 }).default("normal"),
  prefersHighContrast: boolean("prefers_high_contrast").default(false),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Investment Plans
export const investmentPlans = pgTable("investment_plans", {
  id: serial("id").primaryKey(),
  name: varchar("name", { length: 100 }).notNull(),
  description: text("description").notNull(),
  minimumInvestment: decimal("minimum_investment", { precision: 12, scale: 2 }).notNull(),
  monthlyReturnRate: decimal("monthly_return_rate", { precision: 5, scale: 2 }).notNull(),
  durationMonths: integer("duration_months").notNull(),
  riskLevel: varchar("risk_level", { length: 20 }).notNull(),
  fundType: varchar("fund_type", { length: 50 }).notNull().default("crypto"),
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Leads table (existing)
export const leads = pgTable("leads", {
  id: serial("id").primaryKey(),
  name: text("name").notNull(),
  email: text("email").notNull(),
  phone: text("phone"),
  investmentRange: text("investment_range"),
  message: text("message"),
  convertedToUser: boolean("converted_to_user").default(false),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Schema for user insertion
export const insertUserSchema = createInsertSchema(users, {
  email: z.string().email("Please enter a valid email address"),
  fullName: z.string().optional(),
  isAdmin: z.boolean().optional(),
})
.extend({
  password: z.string().min(8, "Password must be at least 8 characters"),
  username: z.string().min(3, "Username must be at least 3 characters"),
})
.omit({ createdAt: true, updatedAt: true });

// Schema for user profile insertion
export const insertUserProfileSchema = createInsertSchema(userProfiles, {
  phone: z.string().optional(),
  country: z.string().optional(),
  preferredLanguage: z.string().optional(),
  riskTolerance: z.string().optional(),
  investmentGoals: z.string().optional(),
  monthlyInvestmentBudget: z.number().optional(),
  prefersDarkMode: z.boolean().optional(),
  preferredFontSize: z.string().optional(),
  prefersHighContrast: z.boolean().optional(),
})
.omit({ createdAt: true, updatedAt: true });

// Schema for investment plan insertion
export const insertInvestmentPlanSchema = createInsertSchema(investmentPlans, {
  name: z.string().min(1, "Plan name is required"),
  description: z.string().min(1, "Plan description is required"),
  minimumInvestment: z.number().min(1, "Minimum investment must be greater than 0"),
  monthlyReturnRate: z.number().min(0, "Monthly return rate must be non-negative"),
  durationMonths: z.number().int().min(1, "Duration must be at least 1 month"),
  riskLevel: z.string().min(1, "Risk level is required"),
  isActive: z.boolean().optional(),
})
.omit({ createdAt: true, updatedAt: true });

// Schema for lead insertion (existing)
export const insertLeadSchema = createInsertSchema(leads, {
  name: z.string().min(1, "Name is required"),
  email: z.string().email("Please enter a valid email address"),
  phone: z.string().optional().nullable(),
  investmentRange: z.string().optional().nullable(),
  message: z.string().optional().nullable(),
})
.omit({ 
  id: true, 
  createdAt: true, 
  updatedAt: true, 
  convertedToUser: true 
});

// Types
export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;
export type InsertUserProfile = z.infer<typeof insertUserProfileSchema>;
export type UserProfile = typeof userProfiles.$inferSelect;
export type InsertInvestmentPlan = z.infer<typeof insertInvestmentPlanSchema>;
export type InvestmentPlan = typeof investmentPlans.$inferSelect;
export type InsertLead = z.infer<typeof insertLeadSchema>;
export type Lead = typeof leads.$inferSelect;

export const investmentPlanSchema = z.object({
  id: z.number(),
  name: z.string(),
  description: z.string(),
  minimumInvestment: z.string(),
  monthlyReturnRate: z.string(),
  durationMonths: z.number(),
  riskLevel: z.enum(["low", "medium", "high"]),
  fundType: z.enum(["crypto", "real-estate", "emerging-tech"]).default("crypto"),
});

