import React, { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { ArrowRight, Coins, CheckCircle, Shield, TrendingUp, AlertCircle, Bitcoin, Building, Cpu } from "lucide-react";
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "../ui/card";
import { Button } from "../ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "../ui/tabs";
import { InvestmentPlan } from "../../../shared/schema";
import { Skeleton } from "../ui/skeleton";
import { useToast } from "../../hooks/use-toast";
import { sampleInvestmentPlans } from "../../data/sample-investment-plans";

export default function InvestmentPlans() {
  const { toast } = useToast();
  const [selectedPlan, setSelectedPlan] = useState<number | null>(null);
  const [activeTab, setActiveTab] = useState("crypto");

  // Fetch investment plans from the API with fallback to sample data
  const { data: apiPlans, isLoading, isError } = useQuery<InvestmentPlan[]>({
    queryKey: ["/api/investment-plans"],
    staleTime: 1000 * 60 * 5, // 5 minutes
  });

  // Use sample data if API fails or returns empty data
  const plans = apiPlans && apiPlans.length > 0 ? apiPlans : sampleInvestmentPlans as InvestmentPlan[];

  // Function to handle plan selection
  const handlePlanSelection = (planId: number) => {
    setSelectedPlan(planId);

    // If user is not logged in, show a toast to prompt login
    toast({
      title: "Autenticação Necessária",
      description: "Por favor, inicie sessão ou registe-se para investir neste plano.",
      variant: "default",
      action: (
        <Button variant="outline" size="sm" onClick={() => window.location.href = "/auth"}>
          Entrar / Registar
        </Button>
      ),
    });
  };

  // Function to get risk level badge color and text
  const getRiskLevelColor = (riskLevel: string) => {
    switch (riskLevel.toLowerCase()) {
      case 'low':
        return "bg-green-100 text-green-800";
      case 'medium':
        return "bg-yellow-100 text-yellow-800";
      case 'high':
        return "bg-red-100 text-red-800";
      default:
        return "bg-gray-100 text-gray-800";
    }
  };

  // Function to translate risk level
  const translateRiskLevel = (riskLevel: string) => {
    switch (riskLevel.toLowerCase()) {
      case 'low':
        return "Baixo Risco";
      case 'medium':
        return "Médio Risco";
      case 'high':
        return "Alto Risco";
      default:
        return "Risco";
    }
  };

  // Function to format currency
  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
    }).format(amount);
  };

  // Function to get fund type icon
  const getFundTypeIcon = (fundType: string) => {
    switch (fundType) {
      case 'crypto':
        return <Bitcoin className="h-5 w-5" />;
      case 'real-estate':
        return <Building className="h-5 w-5" />;
      case 'emerging-tech':
        return <Cpu className="h-5 w-5" />;
      default:
        return <Coins className="h-5 w-5" />;
    }
  };

  // Filter plans by fund type
  const filterPlansByType = (fundType: string) => {
    if (!plans) return [];
    return plans.filter(plan => {
      const planFundType = (plan as any).fundType || 'crypto';
      return planFundType === fundType;
    });
  };

  // Show loading state
  if (isLoading) {
    return (
      <section id="plans" className="py-16 gradient-background">
        <div className="container mx-auto px-4">
          <div className="text-center mb-16">
            <h2 className="text-3xl font-bold mb-4 text-[#FFD700]">Planos de Investimento</h2>
            <p className="text-white max-w-2xl mx-auto">
              A carregar as opções de investimento...
            </p>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {[1, 2, 3].map((i) => (
              <Card key={i} className="shadow-lg">
                <CardHeader>
                  <Skeleton className="h-8 w-2/3 mb-2" />
                  <Skeleton className="h-6 w-full" />
                </CardHeader>
                <CardContent>
                  <Skeleton className="h-6 w-1/2 mb-4" />
                  <Skeleton className="h-4 w-full mb-2" />
                  <Skeleton className="h-4 w-full mb-2" />
                  <Skeleton className="h-4 w-3/4" />
                </CardContent>
                <CardFooter>
                  <Skeleton className="h-10 w-full" />
                </CardFooter>
              </Card>
            ))}
          </div>
        </div>
      </section>
    );
  }

  // Show error state if no plans available
  if (!plans || plans.length === 0) {
    return (
      <section id="plans" className="py-16 gradient-background">
        <div className="container mx-auto px-4">
          <div className="text-center mb-16">
            <h2 className="text-3xl font-bold mb-4 text-[#FFD700]">Planos de Investimento</h2>
            <div className="flex items-center justify-center gap-2 text-red-500">
              <AlertCircle size={20} />
              <p>Não foi possível carregar os planos de investimento. Tente novamente mais tarde.</p>
            </div>
          </div>
        </div>
      </section>
    );
  }

  return (
    <section id="plans" className="py-16 gradient-background">
      <div className="container mx-auto px-4">
        <div className="text-center mb-16">
          <h2 className="text-3xl font-bold mb-4 text-[#FFD700]">Planos de Investimento</h2>
          <p className="text-white max-w-2xl mx-auto">
            Escolha o fundo de investimento que combina com seus objetivos financeiros e tolerância ao risco. 
            As Nossas opções diversificadas oferecem diferentes retornos baseados em duração e nível de risco.
          </p>
        </div>

        {/* Investment Fund Tabs */}
        <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
          <TabsList className="grid w-full grid-cols-3 mb-8">
            <TabsTrigger value="crypto" className="flex items-center gap-2">
              <Bitcoin className="h-4 w-4" />
              Cripto-ativos
            </TabsTrigger>
            <TabsTrigger value="real-estate" className="flex items-center gap-2">
              <Building className="h-4 w-4" />
              Imobiliário
            </TabsTrigger>
            <TabsTrigger value="emerging-tech" className="flex items-center gap-2">
              <Cpu className="h-4 w-4" />
              Tecnologias Emergentes
            </TabsTrigger>
          </TabsList>

          {/* Crypto-assets Tab */}
          <TabsContent value="crypto">
            <div className="text-center mb-8">
              <h3 className="text-xl font-semibold mb-2 text-[#FFD700]">Fundo de Investimento de Cripto-ativos</h3>
              <p className="text-white">
                Invista em uma carteira diversificada de criptomoedas com estratégias de baixo a alto risco.
              </p>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
              {filterPlansByType('crypto').map((plan) => (
                <Card 
                  key={plan.id} 
                  className={`shadow-lg transition-all duration-300 hover:shadow-xl ${
                    selectedPlan === plan.id ? "ring-2 ring-primary" : ""
                  }`}
                >
                  <CardHeader>
                    <div className="flex justify-between items-center mb-2">
                      <CardTitle className="text-xl font-bold flex items-center gap-2">
                        {getFundTypeIcon('crypto')}
                        {plan.name}
                      </CardTitle>
                      <span className={`text-xs px-2 py-1 rounded-full ${getRiskLevelColor(plan.riskLevel)}`}>
                        {translateRiskLevel(plan.riskLevel)}
                      </span>
                    </div>
                    <CardDescription>
                      {plan.description}
                    </CardDescription>
